import React, { Component } from "react";

class Jsonplaceholderscrud extends Component {
  state = {
 posts: [],
  postToEdit :[],
  inputFirst:"",title

}
  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/posts")
    .then(response => response.json()) 
    .then(posts => {
          this.setState({ posts })
    })
  }
 
  render() {
    
    const { posts} = this.state;
    const personsElement = posts.map((person) => (
      <tr key={posts.id}>
          
         <td> {posts.first} </td>
          <td> {posts.last} </td>
      
       
        <td><button
          onClick={() => {
            const newPersons = posts.filter((item) => item.id !== person.id);
            this.setState({ posts: newPersons });
          }}
        >
          Delete
        </button></td>
       <td> <button
          onClick={() => {
            this.setState({ postToEdit: posts });
          }}
        >
          Edit
        </button>
      </td>
      </tr>
    ));
    return (
      <div className="Jsonplaceholderscrud">
        <div>
          first name
          <input
            onChange={(evt) => {
              this.setState({ inputFirst: evt.target.value });
            }}
            defaultValue={postToEdit.first}
          />
          <br />
          last name
          <input
            onChange={(evt) => {
              this.setState({ inputLast: evt.target.value });
            }}
            defaultValue={postToEdit.last}
          />
          <br />
          <button
            onClick={() => {
            //   console.log(`personToEdit.id : ${personToEdit.id}`);
            //   let newPersons = [...persons];
            // we dont 

           
              let person = posts.find((it) => it.id === postToEdit.id);
              person.last = inputLast;
              person.first = inputFirst;
              this.setState({ posts });
            }}
          >
            Update
          </button>
        </div>
        <table>
            <tr>
            <th>
            userId
            </th>
            <th>
            id
            </th>
            <th>
            title
            </th>
            <th>
             body
            </th>
             </tr>
        {personsElement}
       </table>
      </div>
    );
  }
}

export default Jsonplaceholderscrud;
